 import React from 'react'; 
 import Navbar from './navbar';
 import "../assets/Home.css";
 import CustomSlider from "../components/custom.slider";
 const images = [
  { imgURL: 'image1.jpg', imgAlt: 'Image 1 Description' },
  { imgURL: 'image2.jpg', imgAlt: 'Image 2 Description' },
  { imgURL: 'image3.jpg', imgAlt: 'Image 3 Description' }
];
const Home = () => {


  return (

    <div>
      <Navbar/>
      <div class="main-content">
      <div className="App">
      <CustomSlider>
        {images.map((image, index) => {
          return (
            <img
              key={index}
              src={require(`../assets/images/${image.imgURL}`)} // Use relative path here
              alt={image.imgAlt}
            />
          );
        })}
      </CustomSlider>
      </div>
      </div>



    </div>
  );
};

export default Home;
