import together1 from '../assets/image/together1.jpg';
import together2 from '../assets/image/together2.jpg';
import together3 from '../assets/image/together3.jpg';
import together4 from '../assets/image/together4.jpg'; 

import React, { useState, useEffect } from 'react';

const Home = () => {
  const images = [
    together1,
    together2,
    together3,
    together4
  ];

  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // Function to move to the next image
  const goToNextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  // Function to move to the previous image
  const goToPrevImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  // Auto-loop the images using useEffect and setInterval
  useEffect(() => {
    const intervalId = setInterval(() => {
      goToNextImage();
    }, 3000); // Change image every 3 seconds

    // Clear interval when component unmounts
    return () => clearInterval(intervalId);
  }, []);

  return (
    <div>
      <img src={images[currentImageIndex]} alt="slider" style={{ width: '400px', height: '300px' }} />

      <div>
        <button onClick={goToPrevImage}>Previous</button>
        <button onClick={goToNextImage}>Next</button>
      </div>
    </div>
  );
};

export default Home;
